sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || oldValue==newValue) {
      return;
   }
	var getemail=new GlideAjax('GetEmailAddress');
	// Specify the getEmail method
	getemail.addParam('sysparm_name','getEmail');
	var usid=g_form.getValue('u_requested_for');
	// Pass the Requested for sys_id
		getemail.addParam('sysparm_userID',usid);
	getemail.getXML(populateEmailField);
	function populateEmailField(response)
	{
		  var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',emailFromScriptInclude);
		if(!usid)
		{
			g_form.addErrorMessage('The request for value is null');

		}
	{
		g_form.addInfoMessage('The value of requested for  is '+emailFromScriptInclude);
		return
	}
	}


   //Type appropriate comment here, and begin script below

}
